import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/dist/query/react'


export const authApi = createApi(
    {
        reducerPath:'authApi',
        baseQuery:fetchBaseQuery({
            // baseUrl:'http://localhost:1337/api/'
            baseUrl:'/api'

        }),
        endpoints(build){
            return {
                register: build.mutation({
                    query(user) {
                        return {
                            url:'register',
                            method:"post",
                            body:user,
                        }
                    }
                }),
            }
        }
    }
);

export const {
    useRegisterMutation
} = authApi;
